import { Component, OnInit } from '@angular/core';
import { WeatherService } from '../weather.service';

@Component({
  selector: 'app-temperature',
  templateUrl: './temperature.component.html',
  styleUrls: ['./temperature.component.css'],
})
export class TemperatureComponent implements OnInit {
  cities: string = '';
  weatherData: any;

  constructor(private weatherService: WeatherService) {}

  ngOnInit(): void {}

  fetchTemperature() {
    this.weatherService.getTemperature(this.cities).subscribe((data) => {
      this.weatherData = data;
      console.log(data);
    });
  }
}
<div class="container">
  <form>
    <input type="text" name="cities" [(ngModel)]="cities" placeholder="Enter city names separated by commas">
    <button type="button" (click)="fetchTemperature()">Get Temperature</button>
  </form>

  <div *ngIf="weatherData">
    <h2>Weather Information</h2>
    <div *ngFor="let cityWeather of weatherData">
      <h3>{{ cityWeather.name }}</h3>
      <p>Temperature: {{ cityWeather.main.temp }}</p>
      <p>Humidity: {{ cityWeather.main.humidity }}</p>
      <p>Weather: {{ cityWeather.weather[0].description }}</p>
    </div>
  </div>
</div>
