import { Component } from '@angular/core';
import { WeatherService } from '../weather.service';

@Component({
  selector: 'app-temperature',
  templateUrl: './temperature.component.html',
  styleUrls: ['./temperature.component.css'],
})
export class TemperatureComponent {
  city: string = '';
  weatherDataList: any[] = [];

  constructor(private weatherService: WeatherService) {}

  fetchTemperature(): void {
    this.weatherService.getTemperature(this.city).subscribe((response) => {
      this.weatherDataList.push(response);
    });
  }
}
<div class="container">
  <form>
    <input type="text" name="city" [(ngModel)]="city" placeholder="Enter a city name">
    <button type="button" (click)="fetchTemperature()">Get Temperature</button>
  </form>

  <div *ngIf="weatherDataList.length > 0">
    <h2>Weather Data</h2>
    <ul>
      <li *ngFor="let weatherData of weatherDataList">
        <h3>{{ weatherData.name }}</h3>
        <div *ngFor="let weather of weatherData.weather">
          <p>Temperature: {{ weatherData.main?.temp }}</p>
          <p>Humidity: {{ weatherData.main?.humidity }}</p>
          <p>Weather: {{ weather.description }}</p>
        </div>
      </li>
    </ul>
  </div>
</div>
